package com.xworkz.inheritence.external;

public class Flower {

    public Flower() {
        System.out.println("No-arg constructor is running in Flower");
    }

    public void bloom() {
        System.out.println("Flower is blooming");
    }
    public void releaseFragrance() {
        System.out.println("Releasing fragrance");
    }
    public void absorbSunlight() {
        System.out.println("Absorbing sunlight");
    }
    public void attractBees() {
        System.out.println("Attracting bees");
    }
    public void provideNectar() {
        System.out.println("Providing nectar");
    }
}

